package Day10_Array;

public class ArrayPrint {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 single dimension or 1D array
		 */
		
		int [] ram= {10,20,50,65,84};
		
		System.out.println("Value of array is= ");
		
		for(int i=0;i<ram.length;i++) {
			
			System.out.print(ram[i]+ " ");
		}
		System.out.println(" ");
		System.out.println("-------------------------");
		
		System.out.println("& Length of array is "+ram.length);


	}

}
