package Day13_TypesOfBlock;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		byte a=10;
		int b=a;	//converting lower value data type to higher value data type.-Widening casting------->>Implicit
		
		int c=10;
		byte d=(byte)c;	////converting HIgher value data type to Lower value data type.-Narrowing Type casting----->>Explicit

	}

}
